import React, { useState, useEffect, useRef } from 'react';
import { Code2, Database, Terminal, Zap, Briefcase, Mail, MessageCircle, Instagram, ExternalLink, ChevronDown, X, Send, Globe } from 'lucide-react';
import './Portfolio.css';
import './FullPage.css';
import './Interactive.css';

export default function Portfolio() {
  const [currentPage, setCurrentPage] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const [showBudgetPopup, setShowBudgetPopup] = useState(false);
  const [language, setLanguage] = useState('pt'); // pt or en
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });
  const [botSleeping, setBotSleeping] = useState(false);
  const [botBlinking, setBotBlinking] = useState(false);
  const containerRef = useRef(null);
  const lastMoveTime = useRef(Date.now());

  const pages = ['home', 'about', 'skills', 'projects', 'contact'];

  const profileImage = "https://i.imgur.com/JgTexRU.png";

  // Translations
  const translations = {
    pt: {
      developer: 'Developer & Data Analyst',
      specialist: 'Especialista em automaÃ§Ã£o, desenvolvimento de sistemas e soluÃ§Ãµes sob medida para qualquer tipo de projeto',
      viewProjects: 'Ver Projetos',
      contact: 'Contato',
      aboutMe: 'Sobre Mim',
      aboutText1: 'Sou desenvolvedor especializado em',
      aboutText1b: 'automaÃ§Ã£o de processos',
      aboutText1c: 'criaÃ§Ã£o de sistemas sob medida',
      aboutText1d: 'Transformo ideias em soluÃ§Ãµes funcionais e eficientes.',
      aboutText2: 'Do conceito Ã  implementaÃ§Ã£o, desenvolvo',
      aboutText2b: 'qualquer tipo de projeto',
      aboutText2c: 'que envolva automaÃ§Ã£o, anÃ¡lise de dados, aplicaÃ§Ãµes web, sistemas desktop ou integraÃ§Ã£o de APIs. Se pode ser automatizado ou programado, eu posso criar.',
      aboutText3: 'Minha abordagem combina',
      aboutText3b: 'versatilidade tÃ©cnica',
      aboutText3c: 'foco em resultados prÃ¡ticos',
      aboutText3d: 'Cada projeto Ã© Ãºnico e merece uma soluÃ§Ã£o personalizada que realmente resolva o problema.',
      skills: 'Habilidades',
      proficiency: 'proficiency',
      completedProjects: 'Projetos ConcluÃ­dos',
      linesOfCode: 'Linhas de CÃ³digo',
      satisfiedClients: 'Clientes Satisfeitos',
      coffees: 'XÃ­caras de CafÃ©',
      finishedProjects: 'Projetos Finalizados',
      clickToAdd: 'Clique para adicionar screenshot',
      letsTalk: 'Vamos Conversar?',
      contactText: 'Interessado em discutir um projeto ou oportunidade? Entre em contato atravÃ©s dos canais abaixo.',
      inProduction: 'Em produÃ§Ã£o',
      getBudget: 'FaÃ§a seu OrÃ§amento',
      budgetTitle: 'NOVO ORÃ‡AMENTO - TERMINAL',
      budgetName: 'Seu nome',
      budgetEmail: 'Seu email',
      budgetPhone: 'WhatsApp (opcional)',
      budgetProjectType: 'Tipo de projeto',
      budgetDescription: 'Descreva seu projeto',
      budgetDeadline: 'Prazo desejado (opcional)',
      sendViaEmail: 'Enviar por Email',
      sendViaWhatsApp: 'Enviar por WhatsApp',
      projectTypes: {
        automation: 'AutomaÃ§Ã£o de Processos',
        webapp: 'AplicaÃ§Ã£o Web',
        bot: 'Bot/Chatbot',
        analysis: 'AnÃ¡lise de Dados',
        desktop: 'Sistema Desktop',
        other: 'Outro'
      }
    },
    en: {
      developer: 'Developer & Data Analyst',
      specialist: 'Expert in automation, systems development and custom solutions for any type of project',
      viewProjects: 'View Projects',
      contact: 'Contact',
      aboutMe: 'About Me',
      aboutText1: 'I am a developer specialized in',
      aboutText1b: 'process automation',
      aboutText1c: 'custom systems creation',
      aboutText1d: 'I transform ideas into functional and efficient solutions.',
      aboutText2: 'From concept to implementation, I develop',
      aboutText2b: 'any type of project',
      aboutText2c: 'involving automation, data analysis, web applications, desktop systems or API integration. If it can be automated or programmed, I can create it.',
      aboutText3: 'My approach combines',
      aboutText3b: 'technical versatility',
      aboutText3c: 'focus on practical results',
      aboutText3d: 'Each project is unique and deserves a customized solution that truly solves the problem.',
      skills: 'Skills',
      proficiency: 'proficiency',
      completedProjects: 'Completed Projects',
      linesOfCode: 'Lines of Code',
      satisfiedClients: 'Satisfied Clients',
      coffees: 'Cups of Coffee',
      finishedProjects: 'Finished Projects',
      clickToAdd: 'Click to add screenshot',
      letsTalk: "Let's Talk?",
      contactText: 'Interested in discussing a project or opportunity? Contact me through the channels below.',
      inProduction: 'In production',
      getBudget: 'Get a Quote',
      budgetTitle: 'NEW BUDGET - TERMINAL',
      budgetName: 'Your name',
      budgetEmail: 'Your email',
      budgetPhone: 'WhatsApp (optional)',
      budgetProjectType: 'Project type',
      budgetDescription: 'Describe your project',
      budgetDeadline: 'Desired deadline (optional)',
      sendViaEmail: 'Send via Email',
      sendViaWhatsApp: 'Send via WhatsApp',
      projectTypes: {
        automation: 'Process Automation',
        webapp: 'Web Application',
        bot: 'Bot/Chatbot',
        analysis: 'Data Analysis',
        desktop: 'Desktop System',
        other: 'Other'
      }
    }
  };

  const t = translations[language];

  const skills = [
    { name: 'Python & Flask', icon: <Code2 />, level: 95, color: '#00d9ff' },
    { name: 'Excel & VBA', icon: <Database />, level: 90, color: '#a855f7' },
    { name: language === 'pt' ? 'AnÃ¡lise de Dados' : 'Data Analysis', icon: <Terminal />, level: 88, color: '#06b6d4' },
    { name: language === 'pt' ? 'AutomaÃ§Ã£o & Sistemas' : 'Automation & Systems', icon: <Zap />, level: 92, color: '#8b5cf6' },
    { name: language === 'pt' ? 'LogÃ­stica & Trading' : 'Logistics & Trading', icon: <Briefcase />, level: 85, color: '#14b8a6' }
  ];

  const [projects, setProjects] = useState([
    {
      id: 1,
      title: language === 'pt' ? 'Sistema HVI - AnÃ¡lise de AlgodÃ£o' : 'HVI System - Cotton Analysis',
      description: language === 'pt' 
        ? 'AplicaÃ§Ã£o Flask completa para extraÃ§Ã£o e anÃ¡lise de dados HVI de PDFs, com interface visual e exportaÃ§Ã£o Excel.'
        : 'Complete Flask application for HVI data extraction and analysis from PDFs, with visual interface and Excel export.',
      tags: ['Python', 'Flask', 'PDF Processing', 'Data Analysis'],
      image: null
    },
    {
      id: 2,
      title: language === 'pt' ? 'AutomaÃ§Ã£o de Timesheet' : 'Timesheet Automation',
      description: language === 'pt'
        ? 'Sistema VBA integrado para gestÃ£o automatizada de timesheet com geraÃ§Ã£o de PDF e envio por email.'
        : 'Integrated VBA system for automated timesheet management with PDF generation and email sending.',
      tags: ['Excel', 'VBA', 'Automation', 'PDF'],
      image: null
    },
    {
      id: 3,
      title: language === 'pt' ? 'Workflow de AprovaÃ§Ã£o' : 'Approval Workflow',
      description: language === 'pt'
        ? 'Sistema de gestÃ£o de drafts e aprovaÃ§Ãµes para operaÃ§Ãµes de importaÃ§Ã£o/exportaÃ§Ã£o.'
        : 'Draft and approval management system for import/export operations.',
      tags: ['Python', 'Database', 'Workflow'],
      image: null
    }
  ]);

  // Budget form state
  const [budgetForm, setBudgetForm] = useState({
    name: '',
    email: '',
    phone: '',
    projectType: '',
    description: '',
    deadline: ''
  });

  const handleImageUpload = (projectId, event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setProjects(prev => prev.map(p => 
          p.id === projectId ? { ...p, image: reader.result } : p
        ));
      };
      reader.readAsDataURL(file);
    }
  };

  const goToPage = (pageIndex) => {
    if (isTransitioning || pageIndex === currentPage) return;
    if (pageIndex < 0 || pageIndex >= pages.length) return;

    setIsTransitioning(true);
    setCurrentPage(pageIndex);

    setTimeout(() => {
      setIsTransitioning(false);
    }, 800);
  };

  // Mouse tracking
  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePos({ x: e.clientX, y: e.clientY });
      lastMoveTime.current = Date.now();
      setBotSleeping(false);
    };

    window.addEventListener('mousemove', handleMouseMove);

    // Check for sleep
    const sleepCheck = setInterval(() => {
      if (Date.now() - lastMoveTime.current > 3000) {
        setBotSleeping(true);
      }
    }, 500);

    // Random blinking
    const blinkInterval = setInterval(() => {
      if (!botSleeping) {
        setBotBlinking(true);
        setTimeout(() => setBotBlinking(false), 200);
      }
    }, Math.random() * 4000 + 2000);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      clearInterval(sleepCheck);
      clearInterval(blinkInterval);
    };
  }, [botSleeping]);

  // Fullpage navigation
  useEffect(() => {
    const handleWheel = (e) => {
      if (isTransitioning || showBudgetPopup) {
        e.preventDefault();
        return;
      }

      if (e.deltaY > 0) {
        goToPage(currentPage + 1);
      } else {
        goToPage(currentPage - 1);
      }
    };

    const handleKeyDown = (e) => {
      if (showBudgetPopup) return;
      if (e.key === 'ArrowDown') goToPage(currentPage + 1);
      if (e.key === 'ArrowUp') goToPage(currentPage - 1);
    };

    window.addEventListener('wheel', handleWheel, { passive: false });
    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('wheel', handleWheel);
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [currentPage, isTransitioning, showBudgetPopup]);

  const handleSendBudget = (method) => {
    const message = `ðŸš€ NOVO ORÃ‡AMENTO

ðŸ‘¤ Nome: ${budgetForm.name}
ðŸ“§ Email: ${budgetForm.email}
ðŸ“± WhatsApp: ${budgetForm.phone || 'NÃ£o informado'}
ðŸ“‚ Tipo: ${budgetForm.projectType}

ðŸ“ DescriÃ§Ã£o:
${budgetForm.description}

â° Prazo: ${budgetForm.deadline || 'FlexÃ­vel'}`;

    if (method === 'whatsapp') {
      const encoded = encodeURIComponent(message);
      window.open(`https://wa.me/5513988793802?text=${encoded}`, '_blank');
    } else {
      window.location.href = `mailto:gabriel121638@gmail.com?subject=Novo OrÃ§amento - ${budgetForm.name}&body=${encodeURIComponent(message)}`;
    }

    setShowBudgetPopup(false);
    setBudgetForm({
      name: '',
      email: '',
      phone: '',
      projectType: '',
      description: '',
      deadline: ''
    });
  };

  return (
    <div className="fullpage-container" ref={containerRef}>

      {/* Interactive Background */}
      <div className="grid-pattern" style={{
        transform: `translate(${mousePos.x * 0.01}px, ${mousePos.y * 0.01}px)`
      }}></div>
      <div className="gradient-blur gradient-1" style={{
        transform: `translate(${mousePos.x * 0.05}px, ${mousePos.y * 0.05}px)`
      }}></div>
      <div className="gradient-blur gradient-2" style={{
        transform: `translate(${-mousePos.x * 0.03}px, ${-mousePos.y * 0.03}px)`
      }}></div>
      <div className="gradient-blur gradient-3" style={{
        transform: `translate(${mousePos.x * 0.02}px, ${mousePos.y * 0.02}px)`
      }}></div>

      {/* Floating Particles */}
      <div className="floating-particles">
        {[...Array(15)].map((_, i) => (
          <div key={i} className="particle" style={{
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 10}s`,
            animationDuration: `${15 + Math.random() * 10}s`
          }} />
        ))}
      </div>

      {/* Animated Bot Companion */}
      <div 
        className={`bot-companion ${botSleeping ? 'sleeping' : ''}`}
        style={{
          left: `${mousePos.x + 20}px`,
          top: `${mousePos.y + 20}px`
        }}
      >
        <div className="bot-body">
          <div className={`bot-eye left ${botBlinking ? 'blink' : ''}`}></div>
          <div className={`bot-eye right ${botBlinking ? 'blink' : ''}`}></div>
          {botSleeping && <div className="zzz">zzz</div>}
        </div>
      </div>

      {/* Language Toggle */}
      <button 
        className="language-toggle"
        onClick={() => setLanguage(language === 'pt' ? 'en' : 'pt')}
      >
        <Globe size={20} />
        <span>{language === 'pt' ? 'ðŸ‡§ðŸ‡· PT' : 'ðŸ‡ºðŸ‡¸ EN'}</span>
      </button>

      {/* Budget Button */}
      <button 
        className="budget-button"
        onClick={() => setShowBudgetPopup(true)}
      >
        <Terminal size={20} />
        {t.getBudget}
      </button>

      {/* Budget Popup */}
      {showBudgetPopup && (
        <div className="budget-popup-overlay" onClick={() => setShowBudgetPopup(false)}>
          <div className="budget-popup terminal-style" onClick={(e) => e.stopPropagation()}>
            <div className="terminal-header">
              <span>{'>'} {t.budgetTitle}</span>
              <button onClick={() => setShowBudgetPopup(false)}>
                <X size={20} />
              </button>
            </div>

            <div className="terminal-content">
              <div className="terminal-line">
                <span className="prompt">$</span>
                <input
                  type="text"
                  placeholder={t.budgetName}
                  value={budgetForm.name}
                  onChange={(e) => setBudgetForm({...budgetForm, name: e.target.value})}
                  className="terminal-input"
                />
              </div>

              <div className="terminal-line">
                <span className="prompt">$</span>
                <input
                  type="email"
                  placeholder={t.budgetEmail}
                  value={budgetForm.email}
                  onChange={(e) => setBudgetForm({...budgetForm, email: e.target.value})}
                  className="terminal-input"
                />
              </div>

              <div className="terminal-line">
                <span className="prompt">$</span>
                <input
                  type="tel"
                  placeholder={t.budgetPhone}
                  value={budgetForm.phone}
                  onChange={(e) => setBudgetForm({...budgetForm, phone: e.target.value})}
                  className="terminal-input"
                />
              </div>

              <div className="terminal-line">
                <span className="prompt">$</span>
                <select
                  value={budgetForm.projectType}
                  onChange={(e) => setBudgetForm({...budgetForm, projectType: e.target.value})}
                  className="terminal-input"
                >
                  <option value="">{t.budgetProjectType}</option>
                  <option value={t.projectTypes.automation}>{t.projectTypes.automation}</option>
                  <option value={t.projectTypes.webapp}>{t.projectTypes.webapp}</option>
                  <option value={t.projectTypes.bot}>{t.projectTypes.bot}</option>
                  <option value={t.projectTypes.analysis}>{t.projectTypes.analysis}</option>
                  <option value={t.projectTypes.desktop}>{t.projectTypes.desktop}</option>
                  <option value={t.projectTypes.other}>{t.projectTypes.other}</option>
                </select>
              </div>

              <div className="terminal-line">
                <span className="prompt">$</span>
                <textarea
                  placeholder={t.budgetDescription}
                  value={budgetForm.description}
                  onChange={(e) => setBudgetForm({...budgetForm, description: e.target.value})}
                  className="terminal-input terminal-textarea"
                  rows="4"
                />
              </div>

              <div className="terminal-line">
                <span className="prompt">$</span>
                <input
                  type="text"
                  placeholder={t.budgetDeadline}
                  value={budgetForm.deadline}
                  onChange={(e) => setBudgetForm({...budgetForm, deadline: e.target.value})}
                  className="terminal-input"
                />
              </div>

              <div className="terminal-actions">
                <button 
                  onClick={() => handleSendBudget('email')}
                  className="terminal-btn"
                  disabled={!budgetForm.name || !budgetForm.email || !budgetForm.description}
                >
                  <Mail size={18} />
                  {t.sendViaEmail}
                </button>
                <button 
                  onClick={() => handleSendBudget('whatsapp')}
                  className="terminal-btn whatsapp"
                  disabled={!budgetForm.name || !budgetForm.description}
                >
                  <MessageCircle size={18} />
                  {t.sendViaWhatsApp}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Page Indicators */}
      <div className="page-indicators">
        {pages.map((page, index) => (
          <button
            key={page}
            onClick={() => goToPage(index)}
            className={`page-dot ${currentPage === index ? 'active' : ''}`}
            aria-label={page}
          >
            <span className="dot-label">{page}</span>
