import React, { useState, useEffect, useRef } from 'react';
import { Code2, Database, Terminal, Zap, Briefcase, Mail, MessageCircle, Instagram, ExternalLink, ChevronDown } from 'lucide-react';
import './Portfolio.css';
import './FullPage.css';

export default function Portfolio() {
  const [currentPage, setCurrentPage] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const containerRef = useRef(null);

  const pages = ['home', 'about', 'skills', 'projects', 'contact'];

  const profileImage = "/gabriel-foto.png";

  const skills = [
    { name: 'Python & Flask', icon: <Code2 />, level: 95, color: '#00d9ff' },
    { name: 'Excel & VBA', icon: <Database />, level: 90, color: '#a855f7' },
    { name: 'Análise de Dados', icon: <Terminal />, level: 88, color: '#06b6d4' },
    { name: 'Automação & Sistemas', icon: <Zap />, level: 92, color: '#8b5cf6' },
    { name: 'Logística & Trading', icon: <Briefcase />, level: 85, color: '#14b8a6' }
  ];

  const [projects, setProjects] = useState([
    {
      id: 1,
      title: 'Sistema HVI - Análise de Algodão',
      description: 'Aplicação Flask completa para extração e análise de dados HVI de PDFs, com interface visual e exportação Excel.',
      tags: ['Python', 'Flask', 'PDF Processing', 'Data Analysis'],
      image: null
    },
    {
      id: 2,
      title: 'Automação de Timesheet',
      description: 'Sistema VBA integrado para gestão automatizada de timesheet com geração de PDF e envio por email.',
      tags: ['Excel', 'VBA', 'Automation', 'PDF'],
      image: null
    },
    {
      id: 3,
      title: 'Workflow de Aprovação',
      description: 'Sistema de gestão de drafts e aprovações para operações de importação/exportação.',
      tags: ['Python', 'Database', 'Workflow'],
      image: null
    }
  ]);

  const handleImageUpload = (projectId, event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setProjects(prev => prev.map(p => 
          p.id === projectId ? { ...p, image: reader.result } : p
        ));
      };
      reader.readAsDataURL(file);
    }
  };

  const goToPage = (pageIndex) => {
    if (isTransitioning || pageIndex === currentPage) return;
    if (pageIndex < 0 || pageIndex >= pages.length) return;

    setIsTransitioning(true);
    setCurrentPage(pageIndex);

    setTimeout(() => {
      setIsTransitioning(false);
    }, 800);
  };

  useEffect(() => {
    const handleWheel = (e) => {
      if (isTransitioning) {
        e.preventDefault();
        return;
      }

      if (e.deltaY > 0) {
        goToPage(currentPage + 1);
      } else {
        goToPage(currentPage - 1);
      }
    };

    const handleKeyDown = (e) => {
      if (e.key === 'ArrowDown') goToPage(currentPage + 1);
      if (e.key === 'ArrowUp') goToPage(currentPage - 1);
    };

    window.addEventListener('wheel', handleWheel, { passive: false });
    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('wheel', handleWheel);
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [currentPage, isTransitioning]);

  return (
    <div className="fullpage-container" ref={containerRef}>

      <div className="grid-pattern"></div>
      <div className="gradient-blur gradient-1"></div>
      <div className="gradient-blur gradient-2"></div>
      <div className="gradient-blur gradient-3"></div>

      {/* Page Indicators */}
      <div className="page-indicators">
        {pages.map((page, index) => (
          <button
            key={page}
            onClick={() => goToPage(index)}
            className={`page-dot ${currentPage === index ? 'active' : ''}`}
            aria-label={page}
          >
            <span className="dot-label">{page}</span>
          </button>
        ))}
      </div>

      {/* Pages Wrapper */}
      <div 
        className="pages-wrapper"
        style={{ transform: `translateY(-${currentPage * 100}vh)` }}
      >

        {/* HOME PAGE - TAMANHOS REDUZIDOS */}
        <section className="page">
          <div className="page-content">
            <div className="max-w-5xl w-full mx-auto px-6">
              <div className="grid md:grid-cols-2 gap-8 items-center">

                <div className="text-left">
                  <div className="animate-slide-up">
                    <div className="inline-block mb-3">
                      <span className="text-[#00d9ff] font-['JetBrains_Mono'] text-xs tracking-wider">
                        {'<developer>'}
                      </span>
                    </div>
                  </div>

                  <h1 className="text-4xl md:text-5xl font-['JetBrains_Mono'] font-bold mb-4">
                    <span className="neon-text" style={{ color: '#00d9ff' }}>Gabriel</span>
                    <br />
                    <span className="text-white">Corrêa</span>
                  </h1>

                  <div>
                    <p className="text-lg md:text-xl text-gray-400 mb-2 font-['JetBrains_Mono']">
                      Developer & Data Analyst
                      <span className="terminal-cursor"></span>
                    </p>
                    <p className="text-sm text-gray-500 mb-6 max-w-lg">
                      Especialista em automação, desenvolvimento de sistemas e soluções sob medida para qualquer tipo de projeto
                    </p>
                  </div>

                  <div className="flex gap-3">
                    <button onClick={() => goToPage(3)} className="glass-card px-5 py-2.5 inline-flex items-center gap-2 hover:text-[#00d9ff] transition-colors text-sm">
                      <Terminal size={16} />
                      Ver Projetos
                    </button>
                    <button onClick={() => goToPage(4)} className="px-5 py-2.5 inline-flex items-center gap-2 border border-[#00d9ff] rounded-xl hover:bg-[#00d9ff] hover:text-black transition-all text-sm">
                      Contato
                    </button>
                  </div>
                </div>

                <div className="flex justify-center">
                  <div className="profile-image-container">
                    <div className="profile-glow"></div>
                    <img 
                      src={profileImage}
                      alt="Gabriel Corrêa"
                      className="profile-image"
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>

          {currentPage === 0 && (
            <button onClick={() => goToPage(1)} className="scroll-indicator">
              <ChevronDown size={28} />
            </button>
          )}
        </section>

        {/* ABOUT PAGE - TAMANHOS REDUZIDOS */}
        <section className="page">
          <div className="page-content">
            <div className="max-w-3xl w-full mx-auto px-6">
              <div className="glass-card p-8">
                <h2 className="text-3xl font-['JetBrains_Mono'] font-bold mb-6">
                  <span className="text-[#00d9ff]">{'> '}</span>
                  Sobre Mim
                </h2>

                <div className="space-y-4 text-base text-gray-300 leading-relaxed">
                  <p>
                    Sou desenvolvedor especializado em <span className="text-[#00d9ff] font-semibold">automação de processos</span> e 
                    <span className="text-purple-400 font-semibold"> criação de sistemas sob medida</span>. Transformo ideias em soluções funcionais e eficientes.
                  </p>

                  <p>
                    Do conceito à implementação, desenvolvo <span className="text-cyan-400">qualquer tipo de projeto</span> que envolva 
                    automação, análise de dados, aplicações web, sistemas desktop ou integração de APIs. Se pode ser automatizado ou programado, eu posso criar.
                  </p>

                  <p>
                    Minha abordagem combina <span className="text-[#00d9ff]">versatilidade técnica</span> com 
                    <span className="text-purple-400"> foco em resultados práticos</span>. Cada projeto é único e merece uma 
                    solução personalizada que realmente resolva o problema.
                  </p>

                  <div className="flex flex-wrap gap-3 mt-6 pt-6 border-t border-gray-700">
                    <div className="flex items-center gap-2 text-[#00d9ff]">
                      <Code2 size={18} />
                      <span className="text-sm">Python & Flask</span>
                    </div>
                    <div className="flex items-center gap-2 text-purple-400">
                      <Database size={18} />
                      <span className="text-sm">Excel & VBA</span>
                    </div>
                    <div className="flex items-center gap-2 text-cyan-400">
                      <Terminal size={18} />
                      <span className="text-sm">Data Analysis</span>
                    </div>
                    <div className="flex items-center gap-2 text-teal-400">
                      <Briefcase size={18} />
                      <span className="text-sm">Automation</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* SKILLS PAGE - TAMANHOS REDUZIDOS */}
        <section className="page">
          <div className="page-content">
            <div className="max-w-5xl w-full mx-auto px-6">
              <h2 className="text-3xl font-['JetBrains_Mono'] font-bold mb-10 text-center">
                <span className="text-[#00d9ff]">{'> '}</span>
                Habilidades
              </h2>

              <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4 mb-8">
                {skills.map((skill) => (
                  <div key={skill.name} className="glass-card p-6">
                    <div className="flex items-center gap-3 mb-4">
                      <div 
                        className="p-2.5 rounded-lg"
                        style={{ 
                          background: `${skill.color}20`,
                          color: skill.color 
                        }}
                      >
                        {skill.icon}
                      </div>
                      <div className="flex-1">
                        <h3 className="font-['JetBrains_Mono'] font-semibold text-base">
                          {skill.name}
                        </h3>
                        <p className="text-xs text-gray-500">{skill.level}% proficiency</p>
                      </div>
                    </div>

                    <div className="skill-bar">
                      <div 
                        className="skill-fill"
                        style={{ 
                          width: `${skill.level}%`,
                          background: `linear-gradient(90deg, ${skill.color}, ${skill.color}80)`
                        }}
                      />
                    </div>
                  </div>
                ))}
              </div>

              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                {[
                  { label: 'Projetos Concluídos', value: '15+' },
                  { label: 'Linhas de Código', value: '50K+' },
                  { label: 'Clientes Satisfeitos', value: '100%' },
                  { label: 'Xícaras de Café', value: '∞' }
                ].map((stat) => (
                  <div key={stat.label} className="glass-card p-4 text-center">
                    <div className="text-3xl font-['JetBrains_Mono'] font-bold text-[#00d9ff] mb-1">
                      {stat.value}
                    </div>
                    <div className="text-xs text-gray-400">{stat.label}</div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </section>

        {/* PROJECTS PAGE - TAMANHOS REDUZIDOS */}
        <section className="page">
          <div className="page-content">
            <div className="max-w-5xl w-full mx-auto px-6">
              <h2 className="text-3xl font-['JetBrains_Mono'] font-bold mb-10 text-center">
                <span className="text-[#00d9ff]">{'> '}</span>
                Projetos Finalizados
              </h2>

              <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
                {projects.map((project) => (
                  <div key={project.id} className="glass-card project-card overflow-hidden group">
                    <div className="relative">
                      {project.image ? (
                        <div className="relative h-40 overflow-hidden">
                          <img 
                            src={project.image} 
                            alt={project.title}
                            className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                          />
                          <div className="absolute inset-0 bg-gradient-to-t from-[#0a0a0f] via-transparent to-transparent"></div>
                        </div>
                      ) : (
                        <label className="upload-zone block cursor-pointer">
                          <input 
                            type="file" 
                            accept="image/*"
                            onChange={(e) => handleImageUpload(project.id, e)}
                          />
                          <ExternalLink size={32} className="mx-auto mb-2 text-[#00d9ff] opacity-50" />
                          <p className="text-xs text-gray-400">Clique para adicionar screenshot</p>
                        </label>
                      )}
                    </div>

                    <div className="p-5">
                      <h3 className="font-['JetBrains_Mono'] font-bold text-base mb-2 text-white">
                        {project.title}
                      </h3>
                      <p className="text-gray-400 text-xs mb-3 leading-relaxed">
                        {project.description}
                      </p>
                      <div className="flex flex-wrap gap-1.5">
                        {project.tags.map(tag => (
                          <span 
                            key={tag}
                            className="text-xs px-2.5 py-0.5 rounded-full bg-[#00d9ff] bg-opacity-10 text-[#00d9ff] border border-[#00d9ff] border-opacity-30"
                          >
                            {tag}
                          </span>
                        ))}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </section>

        {/* CONTACT PAGE - TAMANHOS REDUZIDOS */}
        <section className="page">
          <div className="page-content">
            <div className="max-w-3xl w-full text-center mx-auto px-6">
              <h2 className="text-3xl font-['JetBrains_Mono'] font-bold mb-6">
                <span className="text-[#00d9ff]">{'> '}</span>
                Vamos Conversar?
              </h2>

              <p className="text-base text-gray-400 mb-8 max-w-2xl mx-auto">
                Interessado em discutir um projeto ou oportunidade? 
                Entre em contato através dos canais abaixo.
              </p>

              <div className="flex flex-wrap gap-4 justify-center">
                <a 
                  href="mailto:gabriel121638@gmail.com" 
                  className="glass-card px-6 py-3 inline-flex items-center gap-2.5 hover:text-[#00d9ff] transition-colors"
                >
                  <Mail size={20} />
                  <span className="font-['JetBrains_Mono']">Email</span>
                </a>

                <a 
                  href="https://wa.me/5513988793802" 
                  target="_blank"
                  rel="noopener noreferrer"
                  className="glass-card px-6 py-3 inline-flex items-center gap-2.5 hover:text-[#25D366] transition-colors"
                >
                  <MessageCircle size={20} />
                  <span className="font-['JetBrains_Mono']">WhatsApp</span>
                </a>

                <div 
                  className="glass-card px-6 py-3 inline-flex items-center gap-2.5 opacity-50 cursor-not-allowed relative group"
                  title="Em produção"
                >
                  <Instagram size={20} />
                  <span className="font-['JetBrains_Mono']">Instagram</span>
                  <span className="absolute -top-10 left-1/2 -translate-x-1/2 bg-[#00d9ff] text-black text-xs px-3 py-1 rounded opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap">
                    Em produção
                  </span>
                </div>
              </div>

              <div className="mt-14 pt-8 border-t border-gray-800">
                <p className="text-gray-500 font-['JetBrains_Mono'] text-xs">
                  {'</developer>'} © 2025 Gabriel Corrêa. Feito com React & Tailwind.
                </p>
              </div>
            </div>
          </div>
        </section>

      </div>
    </div>
  );
}
