import React, { useState, useEffect, useRef } from 'react';
import { Code2, Database, Terminal, Zap, Briefcase, Mail, MessageCircle, Instagram, ExternalLink, ChevronDown, X, Send, Globe } from 'lucide-react';
import './Portfolio.css';
import './FullPage.css';
import './Interactive.css';

export default function Portfolio() {
  const [currentPage, setCurrentPage] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const [showBudgetPopup, setShowBudgetPopup] = useState(false);
  const [language, setLanguage] = useState('pt');
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });
  const [botSleeping, setBotSleeping] = useState(false);
  const [botBlinking, setBotBlinking] = useState(false);
  const containerRef = useRef(null);
  const lastMoveTime = useRef(Date.now());

  const pages = ['home', 'about', 'skills', 'projects', 'contact'];

  const profileImage = "/gabriel-foto.png";

  // Translations
  const translations = {
    pt: {
      developer: 'Developer & Data Analyst',
      specialist: 'Especialista em automa√ß√£o e desenvolvimento de sistemas',
      viewProjects: 'Ver Projetos',
      contact: 'Contato',
      aboutMe: 'Sobre Mim',
      aboutText1: 'Desenvolvedor especializado em',
      aboutText1b: 'automa√ß√£o de processos',
      aboutText1c: 'cria√ß√£o de sistemas',
      aboutText1d: 'Transformo ideias em solu√ß√µes funcionais.',
      aboutText2: 'Desenvolvo projetos que envolvem automa√ß√£o, an√°lise de dados, aplica√ß√µes web, sistemas desktop ou integra√ß√£o de APIs.',
      aboutText3: 'Cada projeto √© √∫nico e merece uma solu√ß√£o personalizada.',
      skills: 'Habilidades',
      proficiency: 'proficiency',
      completedProjects: 'Projetos',
      linesOfCode: 'Linhas',
      satisfiedClients: 'Clientes',
      coffees: 'Caf√©s',
      finishedProjects: 'Projetos',
      clickToAdd: 'Clique para adicionar',
      letsTalk: 'Contato',
      contactText: 'Entre em contato atrav√©s dos canais abaixo',
      inProduction: 'Em produ√ß√£o',
      getBudget: 'Or√ßamento',
      budgetTitle: 'NOVO OR√áAMENTO',
      budgetName: 'Seu nome',
      budgetEmail: 'Seu email',
      budgetPhone: 'WhatsApp (opcional)',
      budgetProjectType: 'Tipo de projeto',
      budgetDescription: 'Descreva seu projeto',
      budgetDeadline: 'Prazo (opcional)',
      sendViaEmail: 'Email',
      sendViaWhatsApp: 'WhatsApp',
      projectTypes: {
        automation: 'Automa√ß√£o',
        webapp: 'Web App',
        bot: 'Bot',
        analysis: 'An√°lise de Dados',
        desktop: 'Desktop',
        other: 'Outro'
      }
    },
    en: {
      developer: 'Developer & Data Analyst',
      specialist: 'Automation and systems development expert',
      viewProjects: 'Projects',
      contact: 'Contact',
      aboutMe: 'About Me',
      aboutText1: 'Developer specialized in',
      aboutText1b: 'process automation',
      aboutText1c: 'systems creation',
      aboutText1d: 'I transform ideas into functional solutions.',
      aboutText2: 'I develop projects involving automation, data analysis, web apps, desktop systems or API integration.',
      aboutText3: 'Each project is unique and deserves a custom solution.',
      skills: 'Skills',
      proficiency: 'proficiency',
      completedProjects: 'Projects',
      linesOfCode: 'Lines',
      satisfiedClients: 'Clients',
      coffees: 'Coffees',
      finishedProjects: 'Projects',
      clickToAdd: 'Click to add',
      letsTalk: 'Contact',
      contactText: 'Get in touch through the channels below',
      inProduction: 'Coming soon',
      getBudget: 'Quote',
      budgetTitle: 'NEW QUOTE',
      budgetName: 'Your name',
      budgetEmail: 'Your email',
      budgetPhone: 'WhatsApp (optional)',
      budgetProjectType: 'Project type',
      budgetDescription: 'Describe your project',
      budgetDeadline: 'Deadline (optional)',
      sendViaEmail: 'Email',
      sendViaWhatsApp: 'WhatsApp',
      projectTypes: {
        automation: 'Automation',
        webapp: 'Web App',
        bot: 'Bot',
        analysis: 'Data Analysis',
        desktop: 'Desktop',
        other: 'Other'
      }
    }
  };

  const t = translations[language];

  const skills = [
    { name: 'Python & Flask', icon: <Code2 />, level: 95, color: '#00d9ff' },
    { name: 'Excel & VBA', icon: <Database />, level: 90, color: '#a855f7' },
    { name: language === 'pt' ? 'An√°lise de Dados' : 'Data Analysis', icon: <Terminal />, level: 88, color: '#06b6d4' },
    { name: language === 'pt' ? 'Automa√ß√£o' : 'Automation', icon: <Zap />, level: 92, color: '#8b5cf6' },
    { name: language === 'pt' ? 'Log√≠stica' : 'Logistics', icon: <Briefcase />, level: 85, color: '#14b8a6' }
  ];

  const [projects, setProjects] = useState([
    {
      id: 1,
      title: language === 'pt' ? 'Sistema HVI' : 'HVI System',
      description: language === 'pt' 
        ? 'Extra√ß√£o e an√°lise de dados HVI de PDFs com interface visual e exporta√ß√£o Excel.'
        : 'HVI data extraction and analysis from PDFs with visual interface and Excel export.',
      tags: ['Python', 'Flask', 'PDF', 'Excel'],
      image: null
    },
    {
      id: 2,
      title: language === 'pt' ? 'Automa√ß√£o Timesheet' : 'Timesheet Automation',
      description: language === 'pt'
        ? 'Sistema VBA para gest√£o de timesheet com gera√ß√£o de PDF e envio autom√°tico.'
        : 'VBA system for timesheet management with PDF generation and auto-send.',
      tags: ['Excel', 'VBA', 'Automation'],
      image: null
    },
    {
      id: 3,
      title: language === 'pt' ? 'Workflow Aprova√ß√£o' : 'Approval Workflow',
      description: language === 'pt'
        ? 'Gest√£o de drafts e aprova√ß√µes para opera√ß√µes de importa√ß√£o/exporta√ß√£o.'
        : 'Draft and approval management for import/export operations.',
      tags: ['Python', 'Database'],
      image: null
    }
  ]);

  // Budget form state
  const [budgetForm, setBudgetForm] = useState({
    name: '',
    email: '',
    phone: '',
    projectType: '',
    description: '',
    deadline: ''
  });

  const handleImageUpload = (projectId, event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setProjects(prev => prev.map(p => 
          p.id === projectId ? { ...p, image: reader.result } : p
        ));
      };
      reader.readAsDataURL(file);
    }
  };

  const goToPage = (pageIndex) => {
    if (isTransitioning || pageIndex === currentPage) return;
    if (pageIndex < 0 || pageIndex >= pages.length) return;

    setIsTransitioning(true);
    setCurrentPage(pageIndex);

    setTimeout(() => {
      setIsTransitioning(false);
    }, 800);
  };

  // Mouse tracking
  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePos({ x: e.clientX, y: e.clientY });
      lastMoveTime.current = Date.now();
      setBotSleeping(false);
    };

    window.addEventListener('mousemove', handleMouseMove);

    const sleepCheck = setInterval(() => {
      if (Date.now() - lastMoveTime.current > 3000) {
        setBotSleeping(true);
      }
    }, 500);

    const blinkInterval = setInterval(() => {
      if (!botSleeping) {
        setBotBlinking(true);
        setTimeout(() => setBotBlinking(false), 200);
      }
    }, Math.random() * 4000 + 2000);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      clearInterval(sleepCheck);
      clearInterval(blinkInterval);
    };
  }, [botSleeping]);

  // Fullpage navigation
  useEffect(() => {
    const handleWheel = (e) => {
      if (isTransitioning || showBudgetPopup) {
        e.preventDefault();
        return;
      }

      if (e.deltaY > 0) {
        goToPage(currentPage + 1);
      } else {
        goToPage(currentPage - 1);
      }
    };

    const handleKeyDown = (e) => {
      if (showBudgetPopup) return;
      if (e.key === 'ArrowDown') goToPage(currentPage + 1);
      if (e.key === 'ArrowUp') goToPage(currentPage - 1);
    };

    window.addEventListener('wheel', handleWheel, { passive: false });
    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('wheel', handleWheel);
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [currentPage, isTransitioning, showBudgetPopup]);

  const handleSendBudget = (method) => {
    const message = `üöÄ NOVO OR√áAMENTO

üë§ Nome: ${budgetForm.name}
üìß Email: ${budgetForm.email}
üì± WhatsApp: ${budgetForm.phone || 'N√£o informado'}
üìÇ Tipo: ${budgetForm.projectType}

üìù Descri√ß√£o:
${budgetForm.description}

‚è∞ Prazo: ${budgetForm.deadline || 'Flex√≠vel'}`;

    if (method === 'whatsapp') {
      const encoded = encodeURIComponent(message);
      window.open(`https://wa.me/5513988793802?text=${encoded}`, '_blank');
    } else {
      window.location.href = `mailto:gabriel121638@gmail.com?subject=Novo Or√ßamento - ${budgetForm.name}&body=${encodeURIComponent(message)}`;
    }

    setShowBudgetPopup(false);
    setBudgetForm({
      name: '',
      email: '',
      phone: '',
      projectType: '',
      description: '',
      deadline: ''
    });
  };

  return (
    <div className="fullpage-container" ref={containerRef}>

      {/* Interactive Background */}
      <div className="grid-pattern" style={{
        transform: `translate(${mousePos.x * 0.01}px, ${mousePos.y * 0.01}px)`
      }}></div>
      <div className="gradient-blur gradient-1" style={{
        transform: `translate(${mousePos.x * 0.05}px, ${mousePos.y * 0.05}px)`
      }}></div>
      <div className="gradient-blur gradient-2" style={{
        transform: `translate(${-mousePos.x * 0.03}px, ${-mousePos.y * 0.03}px)`
      }}></div>
      <div className="gradient-blur gradient-3" style={{
        transform: `translate(${mousePos.x * 0.02}px, ${mousePos.y * 0.02}px)`
      }}></div>

      {/* Floating Particles */}
      <div className="floating-particles">
        {[...Array(10)].map((_, i) => (
          <div key={i} className="particle" style={{
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 10}s`,
            animationDuration: `${15 + Math.random() * 10}s`
          }} />
        ))}
      </div>

      {/* Animated Bot Companion */}
      <div 
        className={`bot-companion ${botSleeping ? 'sleeping' : ''}`}
        style={{
          left: `${mousePos.x + 20}px`,
          top: `${mousePos.y + 20}px`
        }}
      >
        <div className="bot-body">
          <div className={`bot-eye left ${botBlinking ? 'blink' : ''}`}></div>
          <div className={`bot-eye right ${botBlinking ? 'blink' : ''}`}></div>
          {botSleeping && <div className="zzz">zzz</div>}
        </div>
      </div>

      {/* Language Toggle */}
      <button 
        className="language-toggle"
        onClick={() => setLanguage(language === 'pt' ? 'en' : 'pt')}
      >
        <Globe size={18} />
        <span>{language === 'pt' ? 'PT' : 'EN'}</span>
      </button>

      {/* Budget Button */}
      <button 
        className="budget-button"
        onClick={() => setShowBudgetPopup(true)}
      >
        <Terminal size={18} />
        {t.getBudget}
      </button>

      {/* Budget Popup */}
      {showBudgetPopup && (
        <div className="budget-popup-overlay" onClick={() => setShowBudgetPopup(false)}>
          <div className="budget-popup terminal-style" onClick={(e) => e.stopPropagation()}>
            <div className="terminal-header">
              <span>{'>'} {t.budgetTitle}</span>
              <button onClick={() => setShowBudgetPopup(false)}>
                <X size={20} />
              </button>
            </div>

            <div className="terminal-content">
              <div className="terminal-line">
                <span className="prompt">$</span>
                <input
                  type="text"
                  placeholder={t.budgetName}
                  value={budgetForm.name}
                  onChange={(e) => setBudgetForm({...budgetForm, name: e.target.value})}
                  className="terminal-input"
                />
              </div>

              <div className="terminal-line">
                <span className="prompt">$</span>
                <input
                  type="email"
                  placeholder={t.budgetEmail}
                  value={budgetForm.email}
                  onChange={(e) => setBudgetForm({...budgetForm, email: e.target.value})}
                  className="terminal-input"
                />
              </div>

              <div className="terminal-line">
                <span className="prompt">$</span>
                <input
                  type="tel"
                  placeholder={t.budgetPhone}
                  value={budgetForm.phone}
                  onChange={(e) => setBudgetForm({...budgetForm, phone: e.target.value})}
                  className="terminal-input"
                />
              </div>

              <div className="terminal-line">
                <span className="prompt">$</span>
                <select
                  value={budgetForm.projectType}
                  onChange={(e) => setBudgetForm({...budgetForm, projectType: e.target.value})}
                  className="terminal-input"
                >
                  <option value="">{t.budgetProjectType}</option>
                  <option value={t.projectTypes.automation}>{t.projectTypes.automation}</option>
                  <option value={t.projectTypes.webapp}>{t.projectTypes.webapp}</option>
                  <option value={t.projectTypes.bot}>{t.projectTypes.bot}</option>
                  <option value={t.projectTypes.analysis}>{t.projectTypes.analysis}</option>
                  <option value={t.projectTypes.desktop}>{t.projectTypes.desktop}</option>
                  <option value={t.projectTypes.other}>{t.projectTypes.other}</option>
                </select>
              </div>

              <div className="terminal-line">
                <span className="prompt">$</span>
                <textarea
                  placeholder={t.budgetDescription}
                  value={budgetForm.description}
                  onChange={(e) => setBudgetForm({...budgetForm, description: e.target.value})}
                  className="terminal-input terminal-textarea"
                  rows="3"
                />
              </div>

              <div className="terminal-line">
                <span className="prompt">$</span>
                <input
                  type="text"
                  placeholder={t.budgetDeadline}
                  value={budgetForm.deadline}
                  onChange={(e) => setBudgetForm({...budgetForm, deadline: e.target.value})}
                  className="terminal-input"
                />
              </div>

              <div className="terminal-actions">
                <button 
                  onClick={() => handleSendBudget('email')}
                  className="terminal-btn"
                  disabled={!budgetForm.name || !budgetForm.email || !budgetForm.description}
                >
                  <Mail size={16} />
                  {t.sendViaEmail}
                </button>
                <button 
                  onClick={() => handleSendBudget('whatsapp')}
                  className="terminal-btn whatsapp"
                  disabled={!budgetForm.name || !budgetForm.description}
                >
                  <MessageCircle size={16} />
                  {t.sendViaWhatsApp}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Page Indicators */}
      <div className="page-indicators">
        {pages.map((page, index) => (
          <button
            key={page}
            onClick={() => goToPage(index)}
            className={`page-dot ${currentPage === index ? 'active' : ''}`}
            aria-label={page}
          >
            <span className="dot-label">{page}</span>
          </button>
        ))}
      </div>

      {/* Pages Wrapper */}
      <div 
        className="pages-wrapper"
        style={{ transform: `translateY(-${currentPage * 100}vh)` }}
      >

        {/* HOME PAGE */}
        <section className="page">
          <div className="page-content">
            <div className="max-w-6xl w-full mx-auto px-4">
              <div className="grid md:grid-cols-2 gap-8 items-center">

                <div className="text-left">
                  <div className="inline-block mb-3">
                    <span className="text-[#00d9ff] font-['JetBrains_Mono'] text-xs tracking-wider">
                      {'<developer>'}
                    </span>
                  </div>

                  <h1 className="text-4xl md:text-6xl lg:text-7xl font-['JetBrains_Mono'] font-bold mb-4">
                    <span className="neon-text" style={{ color: '#00d9ff' }}>Gabriel</span>
                    <br />
                    <span className="text-white">Corr√™a</span>
                  </h1>

                  <div>
                    <p className="text-lg md:text-xl text-gray-400 mb-2 font-['JetBrains_Mono']">
                      {t.developer}
                      <span className="terminal-cursor"></span>
                    </p>
                    <p className="text-sm text-gray-500 mb-6">
                      {t.specialist}
                    </p>
                  </div>

                  <div className="flex gap-3">
                    <button onClick={() => goToPage(3)} className="glass-card px-5 py-2.5 inline-flex items-center gap-2 hover:text-[#00d9ff] transition-colors text-sm">
                      <Terminal size={16} />
                      {t.viewProjects}
                    </button>
                    <button onClick={() => goToPage(4)} className="px-5 py-2.5 inline-flex items-center gap-2 border border-[#00d9ff] rounded-xl hover:bg-[#00d9ff] hover:text-black transition-all text-sm">
                      {t.contact}
                    </button>
                  </div>
                </div>

                <div className="flex justify-center">
                  <div className="profile-image-container" style={{width: '280px', height: '280px'}}>
                    <div className="profile-glow"></div>
                    <img 
                      src={profileImage}
                      alt="Gabriel Corr√™a"
                      className="profile-image"
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>

          {currentPage === 0 && (
            <button onClick={() => goToPage(1)} className="scroll-indicator">
              <ChevronDown size={28} />
            </button>
          )}
        </section>

        {/* ABOUT PAGE */}
        <section className="page">
          <div className="page-content">
            <div className="max-w-4xl w-full mx-auto px-4">
              <div className="glass-card p-6 md:p-10">
                <h2 className="text-3xl md:text-4xl font-['JetBrains_Mono'] font-bold mb-6">
                  <span className="text-[#00d9ff]">{'> '}</span>
                  {t.aboutMe}
                </h2>

                <div className="space-y-4 text-base text-gray-300 leading-relaxed">
                  <p>
                    {t.aboutText1} <span className="text-[#00d9ff] font-semibold">{t.aboutText1b}</span> {language === 'pt' ? 'e' : 'and'} 
                    <span className="text-purple-400 font-semibold"> {t.aboutText1c}</span>. {t.aboutText1d}
                  </p>

                  <p>
                    {t.aboutText2}
                  </p>

                  <p>
                    {t.aboutText3}
                  </p>

                  <div className="flex flex-wrap gap-3 mt-6 pt-6 border-t border-gray-700">
                    <div className="flex items-center gap-2 text-[#00d9ff] text-sm">
                      <Code2 size={18} />
                      <span>Python</span>
                    </div>
                    <div className="flex items-center gap-2 text-purple-400 text-sm">
                      <Database size={18} />
                      <span>Excel</span>
                    </div>
                    <div className="flex items-center gap-2 text-cyan-400 text-sm">
                      <Terminal size={18} />
                      <span>Data</span>
                    </div>
                    <div className="flex items-center gap-2 text-teal-400 text-sm">
                      <Briefcase size={18} />
                      <span>Automation</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* SKILLS PAGE */}
        <section className="page">
          <div className="page-content">
            <div className="max-w-5xl w-full mx-auto px-4">
              <h2 className="text-3xl md:text-4xl font-['JetBrains_Mono'] font-bold mb-8 text-center">
                <span className="text-[#00d9ff]">{'> '}</span>
                {t.skills}
              </h2>

              <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4 mb-8">
                {skills.map((skill) => (
                  <div key={skill.name} className="glass-card p-5">
                    <div className="flex items-center gap-3 mb-4">
                      <div 
                        className="p-2 rounded-lg"
                        style={{ 
                          background: `${skill.color}20`,
                          color: skill.color 
                        }}
                      >
                        {skill.icon}
                      </div>
                      <div className="flex-1">
                        <h3 className="font-['JetBrains_Mono'] font-semibold text-base">
                          {skill.name}
                        </h3>
                        <p className="text-xs text-gray-500">{skill.level}%</p>
                      </div>
                    </div>

                    <div className="skill-bar">
                      <div 
                        className="skill-fill"
                        style={{ 
                          width: `${skill.level}%`,
                          background: `linear-gradient(90deg, ${skill.color}, ${skill.color}80)`
                        }}
                      />
                    </div>
                  </div>
                ))}
              </div>

              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                {[
                  { label: t.completedProjects, value: '15+' },
                  { label: t.linesOfCode, value: '50K+' },
                  { label: t.satisfiedClients, value: '100%' },
                  { label: t.coffees, value: '‚àû' }
                ].map((stat) => (
                  <div key={stat.label} className="glass-card p-4 text-center">
                    <div className="text-2xl md:text-3xl font-['JetBrains_Mono'] font-bold text-[#00d9ff] mb-1">
                      {stat.value}
                    </div>
                    <div className="text-xs text-gray-400">{stat.label}</div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </section>

        {/* PROJECTS PAGE */}
        <section className="page">
          <div className="page-content">
            <div className="max-w-5xl w-full mx-auto px-4">
              <h2 className="text-3xl md:text-4xl font-['JetBrains_Mono'] font-bold mb-8 text-center">
                <span className="text-[#00d9ff]">{'> '}</span>
                {t.finishedProjects}
              </h2>

              <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-5">
                {projects.map((project) => (
                  <div key={project.id} className="glass-card project-card overflow-hidden group">
                    <div className="relative">
                      {project.image ? (
                        <div className="relative h-40 overflow-hidden">
                          <img 
                            src={project.image} 
                            alt={project.title}
                            className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                          />
                          <div className="absolute inset-0 bg-gradient-to-t from-[#0a0a0f] via-transparent to-transparent"></div>
                        </div>
                      ) : (
                        <label className="upload-zone block cursor-pointer" style={{minHeight: '140px'}}>
                          <input 
                            type="file" 
                            accept="image/*"
                            onChange={(e) => handleImageUpload(project.id, e)}
                          />
                          <ExternalLink size={32} className="mx-auto mb-2 text-[#00d9ff] opacity-50" />
                          <p className="text-xs text-gray-400">{t.clickToAdd}</p>
                        </label>
                      )}
                    </div>

                    <div className="p-4">
                      <h3 className="font-['JetBrains_Mono'] font-bold text-base mb-2 text-white">
                        {project.title}
                      </h3>
                      <p className="text-gray-400 text-xs mb-3 leading-relaxed">
                        {project.description}
                      </p>
                      <div className="flex flex-wrap gap-1.5">
                        {project.tags.map(tag => (
                          <span 
                            key={tag}
                            className="text-xs px-2 py-0.5 rounded-full bg-[#00d9ff] bg-opacity-10 text-[#00d9ff] border border-[#00d9ff] border-opacity-30"
                          >
                            {tag}
                          </span>
                        ))}
                      </div>
                    </div>
